func Abi() *abi.ABI {
	if messageABI != nil {
		return messageABI
	}
	mABI, err := abi.JSON(strings.NewReader(jsondata))
	if err != nil {
		panic(err)
	}
	messageABI = &mABI
	return messageABI
}

type Data struct {
	Target       []byte   `abi:"_target"`
	Sender       []byte   `abi:"_sender"`
	Message      string   `abi:"_message"`
	MessageNonce *big.Int `abi:"messageNonce"`
}

