func UnPacketData(bytesData []byte, data Data) error {
	messageABI = Abi()
	return messageABI.Methods["relayMessage"].Inputs.Unpack(data, bytesData)
	//return messageABI.Unpack(&data, "relayMessage", bytesData)
}
